import React from "react";
import {useLocalStorage} from "./useLocalStorage";

const TaskContext = React.createContext();
 
 function TaskProvide(props){
     
     const {
         item: tasks,
         saveTasks:saveTasks,
         loading,
         error
     } = useLocalStorage('TODOS_V1',[]);
     const [search, setSearchValue] = React.useState('');
     const [openModal,setOpenModal] = React.useState(false );
     const completedTodos = tasks.filter(item => item.completed).length;
     const totalTodos = tasks.length;

     let searchedTodos = [];

     if (!search.length >= 1){
         searchedTodos = tasks;
     }else{
         searchedTodos = tasks.filter(item => {
             const CorLetterMi = item.text.toLowerCase();
             const LetterMinState = search.toLowerCase();
             return CorLetterMi.includes(LetterMinState);
         })
     }


     //Complete
     const CompleteTodo = (text)=>{
         const todoIndex = tasks.findIndex(item => item.text === text);
         const newArrayTodos = [...tasks];
         newArrayTodos[todoIndex].completed = true;
         saveTasks(newArrayTodos);
     }
     //Delete 
     const DeleteTodo = (text)=>{
         const todoIndex = tasks.findIndex(item => item.text === text);
         const newArrayTodos = [...tasks];
         newArrayTodos.splice(todoIndex,1);
         saveTasks(newArrayTodos);
     }
     //Add
     const AddTodo = (text)=>{
         const newArrayTodos = [...tasks];
         newArrayTodos.push({
             completed: false,
             text,
         })
         saveTasks(newArrayTodos);
     }

     /*
         console.log('render antes del efecto');
     
         //react effect
         //en el array especificamos cuando se va a ejecutar //si no tiene array ejecutara cada vez que inicie el render
         React.useEffect(()=>{
             console.log('user effect');
         },[])
     
         console.log('Despues del efect');
         
         
      */
     
     return(
         <TaskContext.Provider value={{
             loading,
             error,
             totalTodos,
             completedTodos,
             search,
             setSearchValue,
             searchedTodos,
             CompleteTodo,
             DeleteTodo,
             AddTodo,
             openModal,
             setOpenModal,
             
         }}>
             {props.children}
         </TaskContext.Provider>
     );
 }
 
export {TaskContext,TaskProvide};
