             //completedTasks,
             search,
             setSearchValue,
             //searchedTasks,
             //CompleteTask,
             DeleteTask,
             AddTask,
             openModal,
             setOpenModal,
             
         }}>
             {props.children}
         </TaskContext.Provider>
     );
 }
 
