import React from "react";
import {TodoContext} from "../../Context/Context";

function TodoForm(){
    const {AddTodo,setOpenModal} = React.useContext(TodoContext);
    const [newValue,setNewValue] = React.useState("");
    const onUpdate = (event) =>{
        setNewValue(event.target.value);
    }
    const onCancel = () =>{
        setOpenModal(false);
    }
    const onAdd = (event) =>{
        event.preventDefault();
       AddTodo(newValue);
        setOpenModal(false);
    }
    return(
        <form className="form" onSubmit={onAdd}>
            <label htmlFor="textTask">Write the new task</label>
            <textarea
                id="textTask"
                value={newValue}
                onChange={onUpdate}
                placeholder="Write"
                cols="5"
            />
            <div>
                <button
                    type="button"
                    onClick={onCancel}
                >
                    Cancelar
                </button>
                
