    const OnDelete = ()=>{
        alert('Borro la tarea ' +props.text );
    };
    return(
        <li className="TodoItem">
           <span 
               className={`Icon Icon-check ${props.completed && 'Icon-check--active'}`}
                onClick={OnCompleted}
           >
