import React from "react";
import {TodoCounter} from "./TodoCounter";
import {TodoItem} from "./TodoItem";
import {CreateTodoButton} from "./CreateTodoButton";
import {TodoList} from "./TodoList";
import {TodoSearch} from "./TodoSearch";

//import './App.css';
const todosItems = [
  {text:"cortar cebolla",completed:true,id: "1"},
  {text:"cortar ",completed:false,id: "2"},
  {text:"cebolla",completed:false,id: "3"}
];
function App() {
  const [search, setSearchValue] = React.useState('');

  const [todos,setTodos] = React.useState(todosItems);

  const completedTodos = todosItems.filter(item => !!item.completed).length;
  const totalTodos = todosItems.length;

    let searchedTodos = [];

    if (!search.length >= 1){
        searchedTodos = todos;
    }else{
        searchedTodos = todos.filter(item => {
            const CorLetterMi = item.text.toLowerCase();
            const LetterMinState = search.toLowerCase();
            return CorLetterMi.includes(LetterMinState);
        })
    }

    const CompleteTodos = (text)=>{
        const todoIndex = todos.findIndex(item => item.text === text);
        const newArrayTodos = [...todos];
        newArrayTodos[todoIndex].completed = true;
        setTodos(newArrayTodos);
    }
    return (
      <React.Fragment>
         <TodoCounter
            total={totalTodos}
            Completed={completedTodos}
         />
         <TodoSearch
             search = {search}
             setSearchValue ={setSearchValue}
         />
          <TodoList>
              {searchedTodos.map(item => (
                  <TodoItem 
                      key={item.id} 
                      text={item.text}
                      completed={item.completed}
                  />
              ))}
          </TodoList>

         <CreateTodoButton/>
        <button>+</button>
      </React.Fragment>
  );
}

export default App;
