import React from "react";
import {useLocalStorage} from "./useLocalStorage";

const TaskContext = React.createContext();
 
 function TaskProvide(props){
     
     const {
         item: task,
         saveTask:saveTask,
         loading,
         error
     } = useLocalStorage('TODOS_V1',[]);
     const [search, setSearchValue] = React.useState('');
     const [openModal,setOpenModal] = React.useState(false );
     const completedTasks = task.filter(item => item.completed).length;
     const totalTasks = task.length;

     let searchedTasks = [];

     if (!search.length >= 1){
         searchedTasks = task;
     }else{
         searchedTasks = task.filter(item => {
             const CorLetterMi = item.text.toLowerCase();
             const LetterMinState = search.toLowerCase();
             return CorLetterMi.includes(LetterMinState);
         })
     }


     //Complete
     const CompleteTask = (text)=>{
         const taskIndex = task.findIndex(item => item.text === text);
         const newArrayTodos = [...task];
         newArrayTodos[taskIndex].completed = true;
         saveTask(newArrayTodos);
     }
     //Delete 
     const DeleteTask = (text)=>{
         const taskIndex = task.findIndex(item => item.text === text);
         const newArrayTodos = [...task];
         newArrayTodos.splice(taskIndex,1);
         saveTask(newArrayTodos);
     }
     //Add
     const AddTask = (text)=>{
         const newArrayTodos = [...task];
         newArrayTodos.push({
             completed: false,
             text,
         })
         saveTask(newArrayTodos);
     }

     /*
         console.log('render antes del efecto');
     
         //react effect
         //en el array especificamos cuando se va a ejecutar //si no tiene array ejecutara cada vez que inicie el render
         React.useEffect(()=>{
             console.log('user effect');
         },[])
     
         console.log('Despues del efect');   
      */
     
     return(
         <TaskContext.Provider value={{
             loading,
             error,
             totalTasks,
             completedTasks,
             search,
             setSearchValue,
             searchedTasks,
             CompleteTask,
             DeleteTask,
             AddTask,
             openModal,
             setOpenModal,
             
         }}>
             {props.children}
         </TaskContext.Provider>
     );
 }
 
export {TaskContext,TaskProvide};
