    React.useEffect(()=>{
        try {
            async function GetTasks (){
                const data = await instance.get('/Tasks');
                console.log(data.data);
            }
            GetTasks();
        }catch (error){
            console.log(`error ${error}`);
        }
        
        
    },[]);
    
     */
    
}

