                    const data = await instance.post('Tasks',{
                        task: infoTemporal,
                        id: infoTemporal
                    })
                    console.log(data.data)
                }
                posData();
            } catch (error){
            }
        },[])
        
        
        try {
            
            const stingfielldTask = JSON.stringify(newTasks);
            localStorage.setItem(itemName,stingfielldTask)
            setItem(newTasks);

        }catch (error){
            setError(error);
        }
    }
    
    
    
    
   


    return {
        data,
        saveTasks,
        loading,
        error
    };
}

export {Connection};
