import React from "react";
import {AppUI} from "./App.UI";

const todosItems = [
  {text:"cortar cebolla",completed:false,id: "1"},
  {text:"cortar ",completed:false,id: "2"},
  {text:"cebolla",completed:false,id: "3"}
];



function useLocalStorage(itemName,initialValue){
    //local storage
    const localStorageItem = localStorage.getItem(itemName);
    let parsedItem;

    if (!localStorageItem){
        localStorage.setItem(itemName,initialValue);
        parsedItem = initialValue;

    }else{
        parsedItem = JSON.parse(localStorageItem);

    }

    //states
    const [item,setItem] = React.useState(parsedItem);

    //save this state in the local storage
    const saveTodos = (newTodos) => {
        const stingfielldTodo = JSON.stringify(newTodos);
        localStorage.setItem(itemName,stingfielldTodo)
        setItem(newTodos);
    }


    return[
        item,
        saveTodos()
    ];



}

function App() {

  const [todos,saveTodos] = useLocalStorage('TODOS_V1',JSON.stringify([]));

  const [search, setSearchValue] = React.useState('');
  const completedTodos = todosItems.filter(item => !!item.completed).length;

  const totalTodos = todosItems.length;
    let searchedTodos = [];

    if (!search.length >= 1){
        searchedTodos = todos;
    }else{
        searchedTodos = todos.filter(item => {
            const CorLetterMi = item.text.toLowerCase();
            const LetterMinState = search.toLowerCase();
            return CorLetterMi.includes(LetterMinState);
        })
    }


    //Complete
    const CompleteTodo = (text)=>{
        const todoIndex = todos.findIndex(item => item.text === text);
        const newArrayTodos = [...todos];
        newArrayTodos[todoIndex].completed = true;
        saveTodos(newArrayTodos);
    }
    //Delete 
    const DeleteTodo = (text)=>{
        const todoIndex = todos.findIndex(item => item.text === text);
        const newArrayTodos = [...todos];
        newArrayTodos.splice(todoIndex,1);
        saveTodos(newArrayTodos);
    }



    return (
      <AppUI
          totalTodos={totalTodos}
          completedTodos={completedTodos}
          search = {search}
          setSearchValue ={setSearchValue}
          searchedTodos={searchedTodos}
          CompleteTodo={CompleteTodo}
          DeleteTodo={DeleteTodo}
      />
  );
}

export default App;
